INSERT INTO `oc_category_description` (`category_id`, `language_id`, `name`, `description`, `meta_title`, `meta_description`, `meta_keyword`) VALUES
(59, 2, 'Vêtement', '&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;b&gt;&amp;nbsp; Une catégorie englobant notre sélection de vêtement pour homme, femme, et enfant pour rester à la mode à prix bas. cette catégorie rassemble les meilleurs marque du domaine de textile.&amp;nbsp;&lt;/b&gt;&lt;br&gt;&lt;/p&gt;', 'Vêtement', '', ''),
(65, 1, 'Beauty and Care', '&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;A selection of sanitary products from the various world leading brands in the field. face and body care products, detergents, as well as cosmetic products.&lt;br&gt;&lt;/p&gt;', 'Beauty and Care', '', ''),
(64, 2, 'Electroménager', '&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Un espace rassemblant notre sélection d’électroménager choisis avec sois des différentes marque mondiales leader du domaine. une liste de produit bien choisis pour rependre a vos attentes de qualité et de robustesse&lt;br&gt;&lt;/p&gt;', 'Electroménager', '', ''),
(66, 2, 'catalogue du mois', '&lt;div&gt;&lt;span style=&quot;font-size: 36px;&quot;&gt;&lt;b&gt;&lt;br&gt;&lt;/b&gt;&lt;/span&gt;&lt;/div&gt;&lt;ul&gt;&lt;li&gt;&lt;span style=&quot;font-size: 36px;&quot;&gt;&lt;b&gt;&lt;a href=&quot;http://localhost/amin2/catalog_janvier.pdf&quot; target=&quot;_blank&quot;&gt;Télécharger ici&lt;/a&gt;&lt;/b&gt;&lt;/span&gt;&lt;/li&gt;&lt;/ul&gt;', 'catalogue du mois', '', ''),
(59, 1, 'Clothes', '&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;b&gt;A category including our selection of clothing for men, women and children to stay fashionable at low prices. this category brings together the best brands in the textile field.&lt;/b&gt;&lt;br&gt;&lt;/p&gt;', 'Clothes', '', ''),
(64, 1, 'Home appliance', '&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;A space bringing together our selection of home appliances chosen with the different world leading brands in the field. a list of products well chosen to meet your expectations of quality and robustness&lt;br&gt;&lt;/p&gt;', 'Home appliance', '', ''),
(65, 2, 'Soin et Beauté', '&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Une sélection de produit sanitaire des différentes marques leader mondiales du domaine. des produit de soin de visage et corps, détergents, ainsi que des produits cosmétiques.&lt;br&gt;&lt;/p&gt;', 'Soin et Beauté', '', ''),
