(1, 3, 'SC Modification', 'SafeCharge', 'SafeCharge', '1.0', 'https://www.safecharge.com/', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n    <name>SC Modification</name>\n    <code>SafeCharge</code>\n    <version>1.0</version>\n    <author>SafeCharge</author>\n    <link>https://www.safecharge.com/</link>\n    \n    <!-- get the SC GW Settings and replace view file if there are SC GW Settings, info() -->\n    <file path=\"admin/controller/sale/order.php\">\n        <operation>\n            <search><![CDATA[$this->response->setOutput($this->load->view(\'sale/order_info\', $data));]]></search>\n            <add position=\"replace\"><![CDATA[\n                // SC Modification\n                $this->load->model(\'setting/setting\');\n                $sc_gw_settings = array();\n                $setting_prefix = \'\';\n                \n                if(is_readable(\n                    DIR_SYSTEM. \'library\' .DIRECTORY_SEPARATOR .\'safecharge\'\n                        . DIRECTORY_SEPARATOR. \'sc_version_resolver.php\')\n                ) {\n                    require_once DIR_SYSTEM. \'library\' .DIRECTORY_SEPARATOR\n                        .\'safecharge\'. DIRECTORY_SEPARATOR. \'sc_version_resolver.php\';\n                    \n                    $setting_prefix = SafeChargeVersionResolver::get_settings_prefix();\n                \n                    $sc_gw_settings = @$this->model_setting_setting->getSetting(\n                        trim($setting_prefix, \'_\')\n                    );\n                }\n                \n                if($sc_gw_settings) {\n                    require_once DIR_SYSTEM. \'config\'. DIRECTORY_SEPARATOR. \'sc_config.php\';\n                    require_once DIR_SYSTEM. \'library\' .DIRECTORY_SEPARATOR\n                        . \'safecharge\'. DIRECTORY_SEPARATOR. \'sc_version_resolver.php\';\n                \n                    $data[\'token_name\'] = SafeChargeVersionResolver::get_token_name();\n                    $data[\'ctr_path\'] = SafeChargeVersionResolver::get_ctr_file_path();\n                    \n                    $data[\'sc_gw_tr_type\']      = @$sc_gw_settings[$setting_prefix . \'transaction_type\'];\n                    $data[\'order_resp_tr_type\'] = \'\';\n                    $data[\'remainingTotal\']     = $order_info[\'total\'];\n                \n                    $query = $this->db->query(\'SELECT * FROM sc_refunds WHERE approved = 1 AND orderId = \' . intval($order_id));\n                    if(@$query->rows) {\n                        $data[\'refunds\'] = $query->rows;\n                    }\n                    else {\n                        $data[\'refunds\'] = array();\n                    }\n\n                    if(!empty($order_info[\'payment_custom_field\'])) {\n                        $payment_custom_fields = is_array($order_info[\'payment_custom_field\'])\n                            ? $order_info[\'payment_custom_field\'] : json_decode($order_info[\'payment_custom_field\'], true);\n\n                        $data[\'order_resp_tr_type\'] = @$payment_custom_fields[SC_GW_P3D_RESP_TR_TYPE]\n                            ? $payment_custom_fields[SC_GW_P3D_RESP_TR_TYPE] : \'\';\n                        \n                        $data[\'paymentMethod\'] = $payment_custom_fields[\'_paymentMethod\'];\n                    }\n                \n                    // prepare and pass refund data to the view\n                    if($data[\'refunds\']) {\n                        foreach($data[\'refunds\'] as $key => $refund) {\n                            $data[\'remainingTotal\'] -= $refund[\'amount\'];\n                            // present amounts as formated minus values\n                            $data[\'refunds\'][$key][\'amount_curr\'] = \'-\' . $this->currency->format(\n                                $refund[\'amount\'],\n                                $order_info[\'currency_code\'],\n                                $order_info[\'currency_value\']\n                            );\n                        }\n                \n                        $data[\'remainingTotalCurr\'] = $this->currency->format(\n                            $data[\'remainingTotal\'],\n                            $order_info[\'currency_code\'],\n                            $order_info[\'currency_value\']\n                        );\n                    }\n\n                    $this->response->setOutput($this->load->view(\'extension/sale/sc_order_info\', $data));\n                }\n                else {\n                    $this->response->setOutput($this->load->view(\'sale/order_info\', $data));\n                }\n                // SC Modification END\n            ]]></add>\n        </operation>\n    </file>\n    \n    <!-- modify file again to preview net Total (after refunds) in the Orders list -->\n    <file path=\"admin/controller/sale/order.php\">\n        <operation>\n            <search><![CDATA[\n                if (isset($this->error[\'warning\'])) {\n            ]]></search>\n            \n            <add position=\"before\"><![CDATA[\n                // SC Modification\n                foreach($results as $key => $order) {\n                    $refunds_sum = 0;\n\n                    $query = $this->db->query(\'SELECT * FROM sc_refunds WHERE orderId = \' . intval($order[\'order_id\']));\n                    if(@$query->rows) {\n                        $sc_refunds = $query->rows;\n                    }\n                    else {\n                        $sc_refunds = array();\n                    }\n\n                    if(is_array($sc_refunds) && $sc_refunds) {\n                        foreach($sc_refunds as $ref) {\n                            $refunds_sum += $ref[\'amount\'];\n                        }\n\n                        $data[\'orders\'][$key][\'total\'] = $this->currency->format(\n                            ($order[\'total\'] - $refunds_sum),\n                            $order[\'currency_code\'],\n                            $order[\'currency_value\']\n                        );\n                    }\n                }\n                // SC Modification END\n            ]]></add>\n        </operation>\n    </file>\n    \n    <!-- modify Orde Model to get from the DB o.payment_custom_field, -->\n    <file path=\"admin/model/sale/order.php\">\n        <operation>\n            <search><![CDATA[CONCAT(o.firstname, \' \', o.lastname) AS customer,]]></search>\n            <add position=\"replace\"><![CDATA[\n                CONCAT(o.firstname, \' \', o.lastname) AS customer, o.payment_custom_field,\n            ]]></add>\n        </operation>\n    </file>\n    \n    <!-- add translations for the buttons logic -->\n    <file path=\"admin/language/en-gb/sale/order.php\">\n        <operation>\n            <search><![CDATA[// Text]]></search>\n            <add position=\"after\"><![CDATA[\n                $_[\'sc_order_confirm_cancel\']   = \'Are you sure, you want to Cancel Order\';\n                $_[\'sc_btn_void\']               = \'Void\';\n                $_[\'sc_order_confirm_settle\']   = \'Are you sure, you want to Settle Order\';\n                $_[\'sc_btn_settle\']             = \'Settle\';\n                $_[\'sc_order_confirm_refund\']   = \'Are you sure, you want to Refund Order\';\n                $_[\'sc_btn_refund\']             = \'Refund via SafeCharge\';\n                $_[\'sc_btn_manual_refund\']      = \'Manual Refund\';\n                $_[\'sc_refund_amount\']          = \'Refund Amount\';\n                $_[\'sc_more_actions\']           = \'More Actions\';\n                $_[\'sc_refund_amount_error\']    = \'The refund amount must be a number bigger than 0!\';\n                $_[\'sc_refund_id\']              = \'Refund ID\';\n                $_[\'sc_date\']                   = \'Date\';\n                $_[\'sc_amount\']                 = \'Amount\';\n                $_[\'sc_remaining_total\']        = \'Remaining Total\';\n            ]]></add>\n        </operation>\n    </file>\n</modification>', 1, '2020-02-03 20:43:57'),
(2, 5, 'Featured Plus', 'featured-plus-adw', 'AlexDW', '1.02', 'https://www.opencart.com/index.php?route=marketplace/extension&filter_member=AlexDW', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\r\n<modification>\r\n	<name>Featured Plus</name>\r\n	<version>1.02</version>\r\n	<author>AlexDW</author>\r\n	<link>https://www.opencart.com/index.php?route=marketplace/extension&amp;filter_member=AlexDW</link>\r\n	<code>featured-plus-adw</code>\r\n	<file path=\"admin/controller/extension/module/featured.php\">\r\n		<operation>\r\n		<search><![CDATA[\r\nif (isset($this->request->post[\'status\']))\r\n			]]></search>\r\n			<add position=\"before\" ><![CDATA[\r\n// Featured+\r\n		$data[\'text_titles\'] = $this->language->get(\'text_titles\');\r\n		$data[\'text_shuffle\'] = $this->language->get(\'text_shuffle\');\r\n\r\n		if (isset($this->request->post[\'titles\'])) {\r\n			$data[\'titles\'] = $this->request->post[\'titles\'];\r\n		} elseif (!empty($module_info) && isset($module_info[\'titles\'])) {\r\n			$data[\'titles\'] = $module_info[\'titles\'];\r\n		} else {\r\n			$data[\'titles\'] = array();\r\n		}\r\n\r\n		if (isset($this->request->post[\'shuffle\'])) {\r\n			$data[\'shuffle\'] = $this->request->post[\'shuffle\'];\r\n		} elseif (!empty($module_info) && isset($module_info[\'shuffle\'])) {\r\n			$data[\'shuffle\'] = 1;\r\n		} else {\r\n			$data[\'shuffle\'] = 0;\r\n		}\r\n\r\n		$this->load->model(\'localisation/language\');\r\n		$data[\'languages\'] = $this->model_localisation_language->getLanguages();\r\n// Featured+\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n\r\n	<file path=\"admin/view/template/extension/module/featured.twig\">\r\n		<operation>\r\n		<search index=\"1\"><![CDATA[\r\n<div class=\"form-group\">\r\n			]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n<!-- Featured+ -->\r\n          <div class=\"form-group\">\r\n            <label class=\"col-sm-2 control-label\" for=\"input-titles\">{{ text_titles }}</label>\r\n            <div class=\"col-sm-10\">\r\n              {% for language in languages %}\r\n              <div class=\"input-group\"><span class=\"input-group-addon\">\r\n			  <img src=\"language/{{ language.code }}/{{ language.code }}.png\" title=\"{{ language.name }}\" />\r\n			  </span>\r\n                <input type=\"text\" name=\"titles[title{{ language.language_id }}]\" value=\"{{ titles[\'title\'~language.language_id] ? titles[\'title\'~language.language_id] : \'\' }}\" placeholder=\"{{ text_titles }}\" class=\"form-control\" />\r\n              </div>\r\n              {% endfor %}\r\n            </div>\r\n          </div>\r\n          <div class=\"form-group\">\r\n            <label class=\"col-sm-2 control-label\" for=\"input-shuffle\">{{ text_shuffle }} </label>\r\n            <div class=\"col-sm-10\">\r\n              <div class=\"checkbox\">\r\n                <label>\r\n                {% if shuffle %}\r\n                <input type=\"checkbox\" name=\"shuffle\" value=\"1\" checked=\"checked\" id=\"input-shuffle\" />\r\n                {% else %}\r\n                <input type=\"checkbox\" name=\"shuffle\" value=\"1\" id=\"input-shuffle\" />\r\n				{% endif %}\r\n                </label>\r\n              </div>\r\n            </div>\r\n          </div>\r\n<!-- Featured+ -->\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"catalog/controller/extension/module/featured.php\">\r\n		<operation>\r\n		<search><![CDATA[\r\n$products =\r\n			]]></search>\r\n			<add position=\"before\" ><![CDATA[\r\n// Featured+\r\n		$lang = $this->config->get(\'config_language_id\');\r\n		$title = isset($setting[\'titles\'][\'title\'.$lang]) && $setting[\'titles\'][\'title\'.$lang] ? $setting[\'titles\'][\'title\'.$lang] : \'\';\r\n\r\n		if (isset($title) && $title) {\r\n			$data[\'heading_title\'] = $title;\r\n		}\r\n\r\n		if (isset($setting[\'shuffle\']) && $setting[\'shuffle\']) {\r\n			shuffle($setting[\'product\']);\r\n		}\r\n// Featured+\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n\r\n	<file path=\"admin/language/en-gb/extension/module/featured.php\">		\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[\r\n<?php\r\n			]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n// Featured+\r\n$_[\'text_titles\']	= \'Header\';\r\n$_[\'text_shuffle\']	= \'Shuffle\';\r\n// Featured+\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"admin/language/ru-ru/extension/module/featured.php\">		\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[\r\n<?php\r\n			]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n// Featured+\r\n$_[\'text_titles\']	= \'Заголовок\';\r\n$_[\'text_shuffle\']	= \'Случайный порядок\';\r\n// Featured+\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n</modification>', 1, '2020-02-03 23:20:40'),
(5, 8, 'Coupon Checkout Page', 'couponcheckoutpage', 'CartBinder', '2.00', 'http://www.cartbinder.com/store/', '<modification>\n  <name>Coupon Checkout Page</name>\n  <version>2.00</version>\n  <link>http://www.cartbinder.com/store/</link>\n  <author>CartBinder</author>\n  <code>couponcheckoutpage</code>\n  <file path=\"catalog/controller/checkout/checkout.php\">\n    <operation>\n      <search><![CDATA[$this->load->language(\'checkout/checkout\');]]></search>\n      <add position=\"after\"><![CDATA[\n      $this->document->addStyle(\'catalog/view/theme/default/stylesheet/couponapply.css\');\n      ]]></add>\n    </operation>\n  </file>\n  <file path=\"catalog/controller/checkout/payment_method.php\">\n    <operation>\n      <search><![CDATA[if (empty($this->session->data[\'payment_methods\'])) {]]></search>\n      <add position=\"before\"><![CDATA[\n        $this->load->language(\'extension/module/couponcheckout\');\n       $data[\'coupon_heading_title\'] =  $this->language->get(\'coupon_title\');\n      $data[\'entry_coupon\'] = $this->language->get(\'text_coupon_entry\');\n\n      if (isset($this->request->post[\'coupon\'])) {\n        $data[\'coupon\'] = $this->request->post[\'coupon\'];     \n      } elseif (isset($this->session->data[\'coupon\'])) {\n        $data[\'coupon\'] = $this->session->data[\'coupon\'];\n      } else {\n        $data[\'coupon\'] = \'\';\n      }\n    $data[\'button_coupon\'] = $this->language->get(\'text_coupon_apply\');\n      ]]></add>\n    </operation>\n    <operation>\n      <search><![CDATA[public function index() {]]></search>\n      <add position=\"before\"><![CDATA[\n       public function validateCoupon() {\n    \n    $this->language->load(\'checkout/cart\'); \n    $this->language->load(\'extension/module/couponcheckout\'); \n    $json = array();    \n    if (!$this->cart->hasProducts()) {\n      $json[\'redirect\'] = $this->url->link(\'checkout/cart\');        \n    } \n        \n    if (isset($this->request->post[\'coupon\'])) {\n      $this->load->model(\'extension/total/coupon\');  \n      $coupon_info = $this->model_extension_total_coupon->getCoupon($this->request->post[\'coupon\']); \n      \n      if ($coupon_info) {     \n        $this->session->data[\'coupon\'] = $this->request->post[\'coupon\'];        \n        $json[\'success\'] = $this->language->get(\'text_couponsuccess\');   \n\n        $this->load->model(\'setting/extension\');\n\n        $totals = array();\n        $taxes = $this->cart->getTaxes();\n        $total = 0;\n\n        // Because __call can not keep var references so we put them into an array.\n        $total_data = array(\n          \'totals\' => &$totals,\n          \'taxes\'  => &$taxes,\n          \'total\'  => &$total\n        );\n          \n        // Display prices\n        if ($this->customer->isLogged() || !$this->config->get(\'config_customer_price\')) {\n          $sort_order = array();\n\n          $results = $this->model_setting_extension->getExtensions(\'total\');\n\n          foreach ($results as $key => $value) {\n            $sort_order[$key] = $this->config->get(\'total_\' . $value[\'code\'] . \'_sort_order\');\n          }\n\n          array_multisort($sort_order, SORT_ASC, $results);\n\n          foreach ($results as $result) {\n            if ($this->config->get(\'total_\' . $result[\'code\'] . \'_status\')) {\n              $this->load->model(\'extension/total/\' . $result[\'code\']);\n\n              // We have to put the totals in an array so that they pass by reference.\n              $this->{\'model_extension_total_\' . $result[\'code\']}->getTotal($total_data);\n            }\n          }\n\n          $sort_order = array();\n\n          foreach ($totals as $key => $value) {\n            $sort_order[$key] = $value[\'sort_order\'];\n          }\n\n          array_multisort($sort_order, SORT_ASC, $totals);\n        }\n\n        $json[\'total\'] = sprintf($this->language->get(\'text_items\'), $this->cart->countProducts() + (isset($this->session->data[\'vouchers\']) ? count($this->session->data[\'vouchers\']) : 0), $this->currency->format($total, $this->session->data[\'currency\']));\n      } else {\n        $json[\'error\'] = $this->language->get(\'error_coupon_expired\');\n      }\n    }\n    \n    $this->response->addHeader(\'Content-Type: application/json\');\n    $this->response->setOutput(json_encode($json));\n  }\n      ]]></add>\n    </operation>\n   \n  </file>\n  <file path=\"catalog/view/theme/*/template/checkout/payment_method.twig\">\n    <operation>\n      <search><![CDATA[{% if text_agree %}]]></search>\n      <add position=\"before\"><![CDATA[\n     <div class=\"cart-module\">\n      <div class=\"cart-heading\">{{ coupon_heading_title }}</div>\n      <div class=\"cart-content\">\n      <label class=\"col-sm-2 control-label\" for=\"input-coupon\">{{ entry_coupon }}</label>\n      <div class=\"col-sm-5\">\n      <input type=\"text\" name=\"coupon\" value=\"{{ coupon }}\" placeholder=\"{{ entry_coupon }}\" id=\"input-coupon\" class=\"form-control\" />\n      <div class=\"pull-right\">\n      <input type=\"button\" value=\"{{ button_coupon }}\" id=\"button-coupon\" data-loading-text=\"{{ text_loading }}\"  class=\"btn btn-primary\" />\n      </div>\n      </div>\n      <div id=\"notifcation\"></div>\n    </div>\n    </div>\n<script>\n$(\'#button-coupon\').on(\'click\', function() {\n  $.ajax({\n    type: \'POST\',\n    url: \'index.php?route=checkout/payment_method/validateCoupon\',\n     data: $(\'input[name=\"coupon\"]\'),\n    dataType: \'json\',   \n    beforeSend: function() {\n      $(\'.coupon-alert\').remove();\n      $(\'#button-coupon\').attr(\'disabled\', true);\n    },\n    complete: function() {\n      $(\'#button-coupon\').attr(\'disabled\', false);\n    },    \n    success: function(json) {\n      $(\'.coupon-alert\').remove();\n     \n      if (json[\'error\']) {\n        $(\'.cart-module\').before(\'<div class=\"coupon-alert alert alert-danger\">\' + json[\'error\'] + \'<button type=\"button\" class=\"close\" data-dismiss=\"alert\">&times;</button></div>\');\n      }\n      \n      if (json[\'success\']) {\n        \n         $(\'.cart-module\').before(\'<div class=\"coupon-alert alert alert-success\">\' + json[\'success\'] + \'<button type=\"button\" class=\"close\" data-dismiss=\"alert\">&times;</button></div>\');\n          \n        setTimeout(function () {\n          $(\'#cart > button\').html(\'<span id=\"cart-total\"><i class=\"fa fa-shopping-cart\"></i> \' + json[\'total\'] + \'</span>\');\n        }, 100);\n\n        $(\'#cart > ul\').load(\'index.php?route=common/cart/info ul li\');\n      }\n    }\n  });\n});\n</script>\n      ]]></add>\n    </operation>\n  </file>\n</modification>', 1, '2020-02-04 03:25:17'),
