			\'tag\'       => $tag,\r\n			\'tag_per\' => $tag_per,\r\n			]]>\r\n			</add>\r\n		</operation>\r\n		\r\n		</file>\r\n		<file path=\"catalog/view/theme/default/template/common/header.twig\">\r\n        <operation>\r\n            <search>\r\n				<![CDATA[\r\n					</head>\r\n				]]>\r\n			</search>\r\n            <add position=\"before\"><![CDATA[\r\n				<style>\r\n				.tag {\r\n    position: absolute;\r\n    margin:5px 20px 0px 0px;\r\n    right:0px;\r\n    font-size: 15px;\r\n    font-weight: 700;\r\n    line-height: 25px;\r\n    width: 55px;\r\n    height: 55px;\r\n    font-variant: small-caps;\r\n    z-index: 100;\r\n    -webkit-border-radius: 50px;\r\n    -moz-border-radius: 50px;\r\n    border-radius: 50px;\r\n    color: #fff;\r\n    text-align: center;\r\n    transition :.6s;\r\n}\r\n.tag:hover{\r\n	transform:rotate(360deg);\r\n}\r\n.tag.hot {\r\n    background: #ff1353;\r\n}\r\n.tag.new {\r\n        background: #18de0d;\r\n        color: #000;\r\n}\r\n.tag.sale {\r\n    background: #fdd922;\r\n    color: #333;\r\n}\r\n</style>\r\n            ]]></add>\r\n        </operation>\r\n		</file>\r\n		\r\n		<file path=\"catalog/view/theme/default/template/product/category.twig\">\r\n		<operation error=\"log\">\r\n			<search index=\"0\"><![CDATA[<div class=\"product-thumb\">]]></search>\r\n			<add position=\"after\">									  \r\n			<![CDATA[\r\n			{% if product.tag_per  %}\r\n			<p class=\"tag {{ random([\'hot\',\'new\',\'sale\']) }}\"><i>Save</i></br>{{ product.tag_per }}%</p>\r\n			{% endif %}\r\n			]]>\r\n			</add>\r\n		</operation>\r\n	    </file>\r\n		\r\n		<file path=\"catalog/view/theme/default/template/product/manufacturer_info.twig\">\r\n		<operation error=\"log\">\r\n			<search index=\"0\"><![CDATA[<div class=\"product-thumb\">]]></search>\r\n			<add position=\"after\">									  \r\n			<![CDATA[\r\n			{% if product.tag_per  %}\r\n			<p class=\"tag {{ random([\'hot\',\'new\',\'sale\']) }}\"><i>Save</i></br>{{ product.tag_per }}%</p>\r\n			{% endif %}\r\n			]]>\r\n			</add>\r\n		</operation>\r\n	    </file>\r\n		\r\n		<file path=\"catalog/view/theme/default/template/product/search.twig\">\r\n		<operation error=\"log\">\r\n			<search index=\"0\"><![CDATA[<div class=\"product-thumb\">]]></search>\r\n			<add position=\"after\">									  \r\n			<![CDATA[\r\n			{% if product.tag_per  %}\r\n			<p class=\"tag {{ random([\'hot\',\'new\',\'sale\']) }}\"><i>Save</i></br>{{ product.tag_per }}%</p>\r\n			{% endif %}\r\n			]]>\r\n			</add>\r\n		</operation>\r\n	    </file>\r\n		\r\n		<file path=\"catalog/view/theme/default/template/extension/module/featured.twig\">\r\n		<operation>\r\n			<search>\r\n				<![CDATA[\r\n					<div class=\"product-thumb transition\">\r\n				]]>\r\n			</search>\r\n			<add position=\"after\">									  \r\n			<![CDATA[\r\n			{% if product.tag_per  %}\r\n			<p class=\"tag {{ random([\'hot\',\'new\',\'sale\']) }}\"><i>Save</i></br>{{ product.tag_per }}%</p>\r\n			{% endif %}\r\n			]]>\r\n			</add>\r\n		</operation>\r\n	    </file>\r\n	\r\n		\r\n</modification>', 1, '2020-02-05 04:20:14'),
