INSERT INTO `oc_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_description`, `meta_keyword`) VALUES
(69, 1, 'Nesquik', '&lt;p&gt;&lt;b&gt;DESCRIPTION&lt;/b&gt;&lt;/p&gt;&lt;p&gt;Instant powder preparation for cocoa drink&lt;/p&gt;&lt;p&gt;Country of manufacture: FRANCE&lt;/p&gt;&lt;p&gt;Nesquik completes the benefits of milk to grow well! With vitamin D to help absorb calcium and phosphorus from milk, necessary for bone growth and development. And iron and zinc to contribute to the functioning of memory and concentration.&lt;/p&gt;&lt;p&gt;Name of operator: Nestlé France&lt;/p&gt;&lt;p&gt;&lt;b&gt;INGREDIENTS&lt;/b&gt;&lt;/p&gt;&lt;p&gt;Sugar, lean cocoa * (21.3%), dextrose, emulsifier: SOY lecithin, minerals: magnesium carbonate, iron pyrophosphate, zinc sulfate, vitamins: C, B1, D - salt, cinnamon, flavors. * From various origins.&lt;/p&gt;&lt;p&gt;&lt;b&gt;Allergens&lt;/b&gt;: SOY&lt;/p&gt;&lt;p&gt;&lt;b&gt;MANUAL&lt;/b&gt;&lt;/p&gt;&lt;ul&gt;&lt;li&gt;The ideal dose: 2 spoons + 200 ml of milk.&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;&lt;b&gt;NUTRITIONAL INFORMATION&lt;/b&gt;&lt;/p&gt;&lt;ul&gt;&lt;li&gt;Average Nutritional Information per 100 ML&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;b&gt;Energy Values&lt;/b&gt;&lt;/p&gt;&lt;ul&gt;&lt;li&gt;Energy Value in Kilojoules&lt;/li&gt;&lt;li&gt;approx. 296 Kj&lt;/li&gt;&lt;li&gt;Energy Value in Kilocalories&lt;/li&gt;&lt;li&gt;approx. 70 Kcal&lt;/li&gt;&lt;li&gt;Nutritional values&lt;/li&gt;&lt;li&gt;Fat&lt;/li&gt;&lt;/ul&gt;', '', 'Nesquik', '', ''),
